INSERT INTO `messages` VALUES ('1181', 'دعاء السجود\r\nسبحان ربي الأعلى \" ، ثلاث مرات .\r\nسبحانك اللهم ربنا وبحمدك ، اللهم اغفر لي.\r\n', '2015-07-24', '', 'http://s.ivas.info/3851292', null, 'OK', '1', '1', '20', '2015-08-02 18:53:01', '2015-08-02 18:53:01', null);
