INSERT INTO `messages` VALUES ('5990', 'دعاء الركوب\r\nبسم الله. الحمد لله. {سُبْحانَ الَّذِي سَخَّرَ لَنَا هَذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ * وَإِنَّا إِلَى رَبِّنَا لَمُنقَلِبُونَ}الحمد لله، الحمد لله، الحمد لله، الله اكبر، الله اكبر، الله اكبر، سبحانك اللهم إني ظلمت نفسي فاغفر لي،إنه لا يغف', '2016-01-15', '', 'http://s.ivas.info/8557627', null, null, '1', '3', '20', '2016-01-04 12:00:21', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('5991', 'دعاء السفر\r\n\"الله أكبر، الله أكبر، الله أكبر،{سُبْحانَ الَّذِي سَخَّرَ لَنَا هَذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ * وَإِنَّا إِلَى رَبِّنَا لَمُنقَلِبُونَ}  \"اللهم إنا نسألُكَ في سفرنا هذا البرَّ والتقوى، ومن العمل ما ترضى، اللهم هون علينا سفرنا هذا واطو ع', '2016-01-16', '', 'http://s.ivas.info/2177628', null, null, '0', '3', '20', '2016-01-04 12:00:37', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('5992', 'دعاء دخول القرية أو البلدة\r\n\"اللهم رب السموات السبع وما أظللن ، ورب الأرضين السبع وما أقللن ،ورب الشياطين وما أضللن ، ورب الرياح وما ذرين.أسألك خير هذه القرية وخير أهلها ، وخير ما فيها،وأعوذ بك من شرها،وشر أهلها ، وشر ما فيها\"\r\n', '2016-01-17', '', 'http://s.ivas.info/1247629', null, null, '0', '3', '20', '2016-01-04 12:00:51', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('5993', 'دعاء دخول السوق\r\n\" لا إله إلا الله وحده لا شريك له،له الملك وله الحمد،يُحيي ويُميت،وهو حي لا يموت،بيده الخير وهو على كل شئ قدير \"\r\n', '2016-01-18', '', 'http://s.ivas.info/2097630', 'http://62.150.25.34:1001/afasycontentsending/Default.aspx?Message=http%3A%2F%2Fs.ivas.info%2F2097630&ArTrack=%D8%AF%D8%B9%D8%A7%D8%A1+%D8%AF%D8%AE%D9%88%D9%84+%D8%A7%D9%84%D8%B3%D9%88%D9%82%0D%0A%22+%D9%84%D8%A7+%D8%A5%D9%84%D9%87+%D8%A5%D9%84%D8%A7+%D8%A7%D9%84%D9%84%D9%87+%D9%88%D8%AD%D8%AF%D9%87+%D9%84%D8%A7+%D8%B4%D8%B1%D9%8A%D9%83+%D9%84%D9%87%D8%8C%D9%84%D9%87+%D8%A7%D9%84%D9%85%D9%84%D9%83+%D9%88%D9%84%D9%87+%D8%A7%D9%84%D8%AD%D9%85%D8%AF%D8%8C%D9%8A%D9%8F%D8%AD%D9%8A%D9%8A+%D9%88%D9%8A%D9%8F%D9%85%D9%8A%D8%AA%D8%8C%D9%88%D9%87%D9%88+%D8%AD%D9%8A+%D9%84%D8%A7+%D9%8A%D9%85%D9%88%D8%AA%D8%8C%D8%A8%D9%8A%D8%AF%D9%87+%D8%A7%D9%84%D8%AE%D9%8A%D8%B1+%D9%88%D9%87%D9%88+%D8%B9%D9%84%D9%89+%D9%83%D9%84+%D8%B4%D8%A6+%D9%82%D8%AF%D9%8A%D8%B1+%22%0D%0A&Channel=BAHRAIN+VIVA&Service=Ruqyah+Shariah&Refid=5993', 'OK', '1', '3', '20', '2016-01-04 12:01:05', '2018-08-28 13:24:26', null);
