INSERT INTO `messages` VALUES ('6004', 'فضل الصلاة على النبي صلى الله عليه وسلم\r\nوقال صلى الله عليه وسلم : \" لا تجعلوا قبري عيداً وصلوا علي ؛ فإن صلاتكم تبلغني حيث كنتم \" \r\n', '2016-01-29', '', 'http://s.ivas.info/9697641', null, null, '0', '3', '20', '2016-01-04 12:03:55', '2018-08-28 13:24:26', null);
