INSERT INTO `messages` VALUES ('6006', 'فضل الصلاة على النبي صلى الله عليه وسلم\r\nوقال صلى الله عليه وسلم \" إن لله ملائكة سياحين في الأرض يبلغوني من أمتي السلام \"\r\n', '2016-01-31', '', 'http://s.ivas.info/7697643', null, null, '0', '3', '20', '2016-01-04 12:04:18', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6007', 'فضل الصلاة على النبي صلى الله عليه وسلم\r\nوقال صلى الله عليه وسلم \" ما من أحد يسلم علي إلا رد الله على روحي حتى أروُدَّ عليه السلام\"\r\n', '2016-02-01', '', 'http://s.ivas.info/2047644', null, null, '0', '3', '20', '2016-01-04 12:04:30', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6008', 'لإفشاء السلام\r\nقال رسول الله صلى الله عليه وسلم :\" لا تدخلوا الجنة حتى تؤمنوا ، ولا تؤمنوا حتى تحابوا أو لا أدلكم على شيءٍ إذا فعلتموه تحاببتم ، أفشوا السلام بينكم \"\r\n', '2016-02-02', '', 'http://s.ivas.info/7697645', null, null, '0', '3', '20', '2016-01-04 12:04:42', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6009', 'لإفشاء السلام\r\nثلاث من جمعهن فقد جمع الإيمان : الإنصاف من نفسك ، وبذل السلام للعالم ،والإنفاق من الإقتار \"\r\n', '2016-02-03', '', 'http://s.ivas.info/4787646', null, null, '0', '3', '20', '2016-01-04 12:04:52', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6010', 'لإفشاء السلام\r\nوعن عبد الله بن عمر رضي الله عنهما : أن رجلاً سأل النبي صلى الله عليه وسلم أي الإسلام خير قال :\" تطعم الطعام وتقرأ السلام على من عرفت ومن لم تعرف \" \r\n', '2016-02-04', '', 'http://s.ivas.info/1607647', null, null, '0', '3', '20', '2016-01-04 12:05:03', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6011', 'كيف يرد السلام على الكافر إذا سلم\r\nإذا سلم عليكم أهل الكتاب فقولوا : وعليكم \"\r\n', '2016-02-05', '', 'http://s.ivas.info/4857648', null, null, '0', '3', '20', '2016-01-04 12:05:15', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6012', 'دعاء صياح الديك ونهيق الحمار\r\n\"إذا سمعتم صياح الديكة ، فاسألوا الله من فضله فإنها رأت ملكاً وإذا سمعتم نهيق الحمار ، فتعوذوا بالله من الشيطان فإنه رأى شيطاناً\r\n', '2016-02-06', '', 'http://s.ivas.info/8897649', null, null, '0', '3', '20', '2016-01-04 12:05:27', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6013', 'دعاء نباح الكلاب بالليل\r\n\"إذا سمعتم نُباح الكلاب ونهيق الحمير بالليل فتعوذوا بالله فإنهن يرين ما لا ترون\"\r\n', '2016-02-07', '', 'http://s.ivas.info/7057650', null, null, '0', '3', '20', '2016-01-04 12:05:39', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6014', 'الدعاء لمن سببته\r\nقال صلى الله عليه وسلم: \"اللَّهُم فأيما مؤمن سببته فاجعل ذلك له قربة إليك يوم القيامة\"\r\n', '2016-02-08', '', 'http://s.ivas.info/2217651', null, null, '0', '3', '20', '2016-01-04 12:05:49', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6015', 'ما يقول المسلم إذا مدح المسلم\r\nقال صلى الله عليه وسلم : \" إذا كان أحدكم مادحاً صاحبه لا محالة فليقل : أحسبُ فلاناً والله حسيبهُ ولا أزكي على الله أحداً أحسبهُ – إن كان يعلم ذاك – كذا وكذا \" \r\n', '2016-02-09', '', 'http://s.ivas.info/2937652', null, null, '0', '3', '20', '2016-01-04 12:06:01', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6016', 'ما يقول المسلم إذا زكي\r\n\"اللهم لا تؤاخذني بما يقولون ، واغفر لي مالا يعلمون [واجعلني خيراً مما يظنون]\"\r\n', '2016-02-10', '', 'http://s.ivas.info/9257653', null, null, '0', '3', '20', '2016-01-04 12:06:14', '2018-08-28 13:24:26', null);
INSERT INTO `messages` VALUES ('6017', 'كيف يلبي المحرم في الحج أو العمرة\r\n\" لبيك اللهم لبيك ، لا شريك لك لبيك ،إن الحمد ،والنعمة ،لك والملك ، لا شريك لك \"\r\n', '2016-02-11', '', 'http://s.ivas.info/5917654', null, null, '0', '3', '20', '2016-01-04 12:06:25', '2018-08-28 13:24:26', null);
